import { Convert } from './convert.js';
export const XmlBase64Converter = {
    get: (value) => {
        if (value) {
            return Convert.ToBase64(value);
        }
        return void 0;
    },
    set: (value) => {
        return Convert.FromBase64(value);
    },
};
export const XmlNumberConverter = {
    get: (value) => {
        if (value) {
            return value.toString();
        }
        return '0';
    },
    set: (value) => {
        return Number(value);
    },
};
export const XmlBooleanConverter = {
    get: (value) => {
        if (value) {
            return value.toString();
        }
        return 'false';
    },
    set: (value) => {
        if (value && value.toLowerCase() === 'true') {
            return true;
        }
        else {
            return false;
        }
    },
};
